import React, { useState } from "react";
import MiniProfile from "./components/MiniProfile";
import FilterButton from "./components/FilterButton"
import "./App.css";

/*const FILTER_MAP_TEAM = {
  All: () => true,
  Favourite: (player) => player.favourite
};
const FILTER_NAMES_TEAM = Object.keys(FILTER_MAP_TEAM);*/

const FILTER_MAP_FAV = {
  All: () => true,
  Favourite: (player) => player.favourite
};
const FILTER_NAMES_FAV = Object.keys(FILTER_MAP_FAV);

function App(props) {
  const [players, setPlayers] = useState(props.players);
  const [filter, setFilter] = useState('All');
  function togglePlayerFav(id) {
    const updatedPlayers = players.map((player) => {
      if (id === player.id) {
        return {...player, favourite: !player.favourite}
      }
      return player;
    });
    setPlayers(updatedPlayers);
  }   
  const playerList = players
  .filter(FILTER_MAP_FAV[filter])
  .map((player) => (
    <MiniProfile
      id={player.id}
      name={player.name}
      ppg={player.ppg}
      favourite={player.favourite}
      togglePlayerFav={togglePlayerFav}
    />
  ));
  const filterList = FILTER_NAMES_FAV.map((name) => (
    <FilterButton
      key={name}
      name={name}
      isPressed={name === filter}
      setFilter={setFilter}
    />
  ));  
  return (
    <div className="1">
      <h1>NBA Players</h1>
        <div className="filter">
          {filterList}
        </div>
        <div className="player-list">
          <ul
            className="2"
            aria-labelledby="list-heading"
          >
            {playerList.length === 0 && "Empty List"}
            {playerList}
          </ul>
        </div>
    </div>
  );
}

export default App;
